DROP TABLE IF EXISTS `lost_item`;
CREATE TABLE `lost_item` (
  `id` int(10) UNSIGNED NOT NULL,
  `title` varchar(255) NOT NULL,
  `text` longtext NOT NULL,
  `photo` varchar(255) NOT NULL,
  `presence_indicator` tinyint(1) NOT NULL,
  `user_id` int(10) UNSIGNED NOT NULL,
  `created_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `rules` tinyint(1) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

--
-- Дамп данных таблицы `lost_item`
--

INSERT INTO `lost_item` (`id`, `title`, `text`, `photo`, `presence_indicator`, `user_id`, `created_at`, `rules`) VALUES
(57, 'Наушники Apple AirPods', 'Сегодня где-то в колледже потерял наушники Apple AirPods (2-го поколения). Заметил пропажу после пары в кабинете 3.30. До этого была пара в 1.14. Если вы их нашли, то свяжитесь со мной как можно скорее, пожалуйста!', '/img/lost_item2.jpg', 0, 21, '2023-06-01 19:51:09', 0),
(58, 'Карта Visa Сбербанка', 'Нашел банковскую карту Visa возле турникетов на выходе из колледжа, оставил на посту охраны.  Карта именная, так что если она ваша, то просто скажите свое имя и фамилию, и вам ее отдадут.', '/img/lost_item6.jpg', 1, 21, '2023-06-03 19:55:58', 1),
(59, 'Черный мужской кошелек', 'Дома обнаружил пропажу кошелька, возможно оставил в колледже, но где точно – не знаю. Если кто-то видел его, то передайте, пожалуйста, в студсовет, либо свяжитесь со мной по указанному электронному адресу, спасибо!', '/img/lost_item3.jpg', 0, 21, '2023-06-08 19:59:38', 0),
(60, 'Серебряные серьги', 'Вчера где-то в колледже потеряла серьги (как на картинке), если кто найдет, напишите мне.', '/img/lost_item5.jpg', 0, 21, '2023-06-09 07:09:29', 0);

